
-- Liberar compras que tÃªm payment_id confirmado via webhook (payment.updated)
UPDATE purchases 
SET status = 'completed'
WHERE status = 'pending'
  AND stripe_payment_intent_id LIKE '%|mp:%'
  AND SPLIT_PART(stripe_payment_intent_id, '|mp:', 2) IN (
